
public class ArraysBidimensionales {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int [][] matrix = new int [4][5];
		
		matrix[0][0] = 4;
		matrix[0][1] = 4;
		matrix[0][2] = 4;
		matrix[0][3] = 4;
		matrix[0][4] = 4;
		
		matrix[1][0] = 4;
		matrix[1][1] = 4;
		matrix[1][2] = 4;
		matrix[1][3] = 4;
		matrix[1][4] = 4;
		
		matrix[2][0] = 4;
		matrix[2][1] = 4;
		matrix[2][2] = 4;
		matrix[2][3] = 4;
		matrix[2][4] = 4;
		
		matrix[3][0] = 4;
		matrix[3][1] = 4;
		matrix[3][2] = 4;
		matrix[3][3] = 4;
		matrix[3][4] = 4;
		
		for(int i=0; i<4;i++){
			
			System.out.println();
			
			for(int j=0; j<5; j++){
				
				System.out.print(matrix[i][j]);
				
			}
			
		}
	}

}
