
public class manipulaCadenas3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		String alumno1, alumno2;
		
		alumno1 = "Andres";
		alumno2 = "andres";
		
		System.out.println(alumno1.equals(alumno2));//Distingue mayusculas
		System.out.println(alumno1.equalsIgnoreCase(alumno2));//No tiene en cuenta mayusculas y minusculas
	}

}
